func getMoviesExample(path: Path, quantity: Int = 5)->[Movie]{
    var movies = [Movie]()
    for index in 1...quantity{
        let movie = Movie(backdropPath: "backdropPath", id: index, overview: "overview movie \(index) \(path)", popularity: 5.7, posterPath: "posterPath", releaseDate: "10/05/2021", title: "Movie \(index) \(path)", voteAverage: 8.5, voteCount: 200)
        movies.append(movie)
    }
    return movies
}
